function Get-TextFromMarkdown($PathToMdFile)
{
    [PSCustomObject]$obj = [ordered]@{}
    $ParsedContent = @()
    try {
        # Use -Raw so we can see how many lines were parsed, otherwise we only get an error on the first line
        $ParsedContent = ConvertFrom-Markdown -InputObject (Get-Content $PathToMdFile) -Raw
    }
    catch {
        Write-Warning "Failed to convert markdown file '$PathToMdFile'. Error message: $_"
    }

    $obj | Add-Member -MemberType NoteProperty -Name 'FileName' -Value ([IO.Path]::GetFileNameWithoutExtension($PathToMdFile))
    $obj | Add-Member -MemberType NoteProperty -Name 'LineCount' -Value ($ParsedContent.Count)
    return $obj
}

